tags.map:
function(doc) {
   if (doc.type && doc.type != "Compte") {
 	return ;
   }
   for (type in doc.tags) {
     for(idtag in doc.tags[type]) {
 	    emit([type, doc.tags[type][idtag]], doc.origines);
     }
   }
 }

all.map:
function(doc) {
   	if (doc.type == "Compte") {
   		emit([doc.interpro, 
                           doc.statut,
   			  doc._id, 
   			  doc.nom_a_afficher, 
 		  	  doc.identifiant, 
 		  	  doc.adresse, 
 		  	  doc.commune, 
 		      doc.code_postal, doc.compte_type], null);
  	}
 }
